const nav=document.querySelector(".nav"),headerButton=document.querySelector(".header__button");headerButton.addEventListener("click",(()=>{nav.classList.contains("nav--closed")?(nav.classList.remove("nav--closed"),headerButton.classList.remove("header__button--close"),headerButton.classList.add("header__button--open")):(nav.classList.add("nav--closed"),headerButton.classList.add("header__button--close"),headerButton.classList.remove("header__button--open"))}));